import React from "react";
import { connect } from "react-redux";

import Card from "../Card";
import mapStateToProps from "../mapStateToProps";
import {
  alterarNumeroMinimo,
  alterarNumeroMaximo,
} from "../../store/actions/numeros";
import "./style.css";

function Intervalo(props) {
  const { min, max } = props;

  function handleMinimo(e) {
    e.preventDefault();
    props.alterarMinimo(e.target.value);
  }

  function handleMaximo(e) {
    e.preventDefault();
    props.alterarMinimo(e.target.value);
  }

  return (
    <Card title="Intervalo de Números" red>
      <div className="Intervalo">
        <span>
          <strong>Mínimo:</strong>
          <input type="number" value={min} onChange={(e) => handleMinimo()} />
        </span>
        <span>
          <strong>Máximo:</strong>
          <input type="number" value={max} onChange={(e) => handleMaximo()} />
        </span>
      </div>
    </Card>
  );
}

function mapActionCreatorToProps(dispatch) {
  return {
    alterarMinimo(novoNumero) {
      // action creator -> action
      const action = alterarNumeroMinimo(novoNumero);
      dispatch(action);
    },
    alterarMaximo(novoNumero) {
      // action creator -> action
      const action = alterarNumeroMaximo(novoNumero);
      dispatch(action);
    },
  };
}

export default connect(mapStateToProps, mapActionCreatorToProps)(Intervalo);
